INSERT INTO `album_intro` (`id`, `description`, `background_image`, `status`, `created`, `modified`) VALUES
(1, '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<div style=\"height: 400px;\">\r\n<div style=\"color: #f47a00; font-size: 30px; text-align: center;\">&nbsp;</div>\r\n<div style=\"color: #f47a00; font-size: 30px; text-align: center;\">&nbsp;</div>\r\n<div style=\"color: #f47a00; font-size: 30px; text-align: center;\"><span style=\"font-size: 30pt;\">Gallery</span></div>\r\n<div style=\"color: white; text-align: left; font-size: 20px;\"><span style=\"font-size: 11pt;\">Pin Sing Travel &amp; Tours have organized multiple successful large scale tours across the globe. Here are some of our key highlight and stories. Contact us to find out how you can start your own travel journey with us!</span></div>\r\n</div>\r\n</body>\r\n</html>', 'photo/61960e1903af3.jpg', '1', '2021-10-21 11:59:50', '2021-11-25 11:26:40'),
(12, '<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p style=\"line-height: 1;\"><span style=\"font-size: 30pt; color: #000000;\">Travel &amp; Tour</span><br /><span style=\"font-size: 30pt;\"><span style=\"color: #f0f0f0;\"><span style=\"color: #000000;\">Grab</span> Amazing </span> <br /><span style=\"color: #000000;\">Promotion</span></span></p>\r\n<p><span style=\"color: #f0f0f0;\">It&rsquo;s saqid that to travel is to live, and at the end of the day, people forget the years and remember moments.</span></p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</body>\r\n</html>', 'photo/619efcd3c4e87.jpg', NULL, '2021-11-25 10:55:07', '2021-11-25 11:25:38');
